import { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const AuthPage = () => {
  const [activeCard, setActiveCard] = useState('login'); // 'login' or 'register'
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    role: 'Employee'
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const { login, register } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const result = await login(formData.email, formData.password);

    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.message);
    }

    setLoading(false);
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const result = await register(formData);

    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.message);
    }

    setLoading(false);
  };

  const resetForm = () => {
    setFormData({
      email: '',
      password: '',
      name: '',
      role: 'Employee'
    });
    setError('');
  };

  return (
    <div style={styles.container}>
      {/* Header Section */}
      <div style={styles.header}>
        <div style={styles.logo}>
          <div style={styles.logoIcon}>üíº</div>
          <h1 style={styles.title}>HRMS Pro</h1>
        </div>
        <p style={styles.subtitle}>Choose your authentication method</p>
      </div>

      {/* Two Cards Section */}
      <div style={styles.cardsContainer}>
        {/* Login Card */}
        <div 
          style={{
            ...styles.card,
            ...(activeCard === 'login' ? styles.cardActive : {})
          }}
          onClick={() => {
            setActiveCard('login');
            resetForm();
          }}
        >
          <div style={styles.cardIcon}>üîê</div>
          <h3 style={styles.cardTitle}>Sign In</h3>
          <p style={styles.cardDescription}>
            Access your existing HRMS account with your credentials
          </p>
          <div style={styles.cardFeatures}>
            <span style={styles.feature}>Employee & Admin Access</span>
            <span style={styles.feature}>Secure Authentication</span>
            <span style={styles.feature}>Quick Access</span>
          </div>
          <div style={styles.cardAction}>
            Click to Sign In <span style={styles.arrow}>‚Üí</span>
          </div>
          <div style={styles.cardBadge}>Existing Users</div>
        </div>

        {/* Register Card */}
        <div 
          style={{
            ...styles.card,
            ...(activeCard === 'register' ? styles.cardActive : {})
          }}
          onClick={() => {
            setActiveCard('register');
            resetForm();
          }}
        >
          <div style={styles.cardIcon}>üöÄ</div>
          <h3 style={styles.cardTitle}>Create Account</h3>
          <p style={styles.cardDescription}>
            Join your organization's HRMS platform with a new account
          </p>
          <div style={styles.cardFeatures}>
            <span style={styles.feature}>Employee/Admin Roles</span>
            <span style={styles.feature}>Quick Setup</span>
            <span style={styles.feature}>24/7 Support</span>
          </div>
          <div style={styles.cardAction}>
            Click to Register <span style={styles.arrow}>‚Üí</span>
          </div>
          <div style={styles.cardBadgeRegister}>New Users</div>
        </div>
      </div>

      {/* Form Section */}
      {activeCard && (
        <div style={styles.formContainer}>
          <div style={styles.formCard}>
            {/* Form Header */}
            <div style={styles.formHeader}>
              <div style={styles.formIcon}>
                {activeCard === 'login' ? 'üîê' : 'üöÄ'}
              </div>
              <h2 style={styles.formTitle}>
                {activeCard === 'login' ? 'Sign In to HRMS' : 'Create HRMS Account'}
              </h2>
              <p style={styles.formSubtitle}>
                {activeCard === 'login' 
                  ? 'Enter your credentials to access your account' 
                  : 'Fill in your details to create a new account'
                }
              </p>
            </div>

            {error && (
              <div style={styles.error}>
                <span style={styles.errorIcon}>‚ö†Ô∏è</span>
                {error}
              </div>
            )}

            <form onSubmit={activeCard === 'login' ? handleLogin : handleRegister} style={styles.form}>
              {activeCard === 'register' && (
                <div style={styles.formGroup}>
                  <label style={styles.label}>Full Name</label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                    style={styles.input}
                    placeholder="Enter your full name"
                  />
                </div>
              )}

              <div style={styles.formGroup}>
                <label style={styles.label}>Email Address</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                  style={styles.input}
                  placeholder={activeCard === 'login' ? "Enter your email" : "Enter your work email"}
                />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Password</label>
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  required
                  minLength={activeCard === 'register' ? "6" : undefined}
                  style={styles.input}
                  placeholder={
                    activeCard === 'login' 
                      ? "Enter your password" 
                      : "Create a password (min 6 characters)"
                  }
                />
              </div>

              {activeCard === 'register' && (
                <div style={styles.formGroup}>
                  <label style={styles.label}>Account Type</label>
                  <select
                    name="role"
                    value={formData.role}
                    onChange={handleChange}
                    style={styles.select}
                  >
                    <option value="Employee">üë®‚Äçüíº Employee Account</option>
                    <option value="Admin">üëë Administrator Account</option>
                  </select>
                  <p style={styles.roleHint}>
                    {formData.role === 'Employee' 
                      ? 'Standard access for team members' 
                      : 'Full administrative privileges'
                    }
                  </p>
                </div>
              )}

              {activeCard === 'login' && (
                <div style={styles.formOptions}>
                  <label style={styles.remember}>
                    <input type="checkbox" style={styles.checkbox} />
                    Remember me
                  </label>
                  <a href="/forgot-password" style={styles.forgotLink}>
                    Forgot password?
                  </a>
                </div>
              )}

              <button 
                type="submit" 
                style={{
                  ...styles.button,
                  ...(loading ? styles.buttonLoading : {})
                }}
                disabled={loading}
              >
                {loading ? (
                  <>
                    <span style={styles.spinner}></span>
                    {activeCard === 'login' ? 'Signing In...' : 'Creating Account...'}
                  </>
                ) : (
                  activeCard === 'login' ? 'Sign In' : 'Create Account'
                )}
              </button>
            </form>

            <div style={styles.securityNote}>
              <span style={styles.securityIcon}>üîí</span>
              Your data is securely encrypted and protected
            </div>
          </div>
        </div>
      )}

      {/* Footer Note */}
      <div style={styles.footerNote}>
        <p>Select an option above to {activeCard ? 'proceed' : 'get started'}</p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    padding: '40px 20px',
    fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif",
  },
  header: {
    textAlign: 'center',
    marginBottom: '60px',
  },
  logo: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '16px',
    marginBottom: '16px',
  },
  logoIcon: {
    fontSize: '3rem',
  },
  title: {
    fontSize: '3rem',
    fontWeight: '700',
    color: 'white',
    margin: 0,
    textShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  },
  subtitle: {
    fontSize: '1.25rem',
    color: 'rgba(255, 255, 255, 0.9)',
    margin: 0,
    fontWeight: '500',
  },
  cardsContainer: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
    gap: '30px',
    maxWidth: '800px',
    margin: '0 auto 40px',
  },
  card: {
    background: 'rgba(255, 255, 255, 0.95)',
