    textAlign: 'center',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    border: '3px solid transparent',
    position: 'relative',
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
    backdropFilter: 'blur(10px)',
  },
  cardActive: {
    border: '3px solid #667eea',
    transform: 'translateY(-5px)',
    boxShadow: '0 20px 40px rgba(0, 0, 0, 0.15)',
  },
  cardIcon: {
    fontSize: '3.5rem',
    marginBottom: '20px',
  },
  cardTitle: {
    fontSize: '1.5rem',
    fontWeight: '700',
    margin: '0 0 15px 0',
    color: '#1f2937',
  },
  cardDescription: {
    fontSize: '0.95rem',
    color: '#6b7280',
    lineHeight: '1.6',
    margin: '0 0 25px 0',
    flex: 1,
  },
  cardFeatures: {
    display: 'flex',
    flexDirection: 'column',
    gap: '8px',
    marginBottom: '25px',
  },
  feature: {
    fontSize: '0.85rem',
    color: '#667eea',
    backgroundColor: '#f0f4ff',
    padding: '6px 12px',
    borderRadius: '20px',
    fontWeight: '500',
  },
  cardAction: {
    color: '#667eea',
    fontWeight: '600',
    fontSize: '0.9rem',
    marginBottom: '15px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '8px',
  },
  cardBadge: {
    background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
