  },
  arrow: {
    fontSize: '1.1rem',
    transition: 'transform 0.2s ease',
  },
  formContainer: {
    maxWidth: '480px',
    margin: '0 auto 30px',
  },
  formCard: {
    background: 'white',
    padding: '40px',
    borderRadius: '20px',
    boxShadow: '0 20px 40px rgba(0, 0, 0, 0.1)',
  },
  formHeader: {
    textAlign: 'center',
    marginBottom: '32px',
  },
  formIcon: {
    fontSize: '4rem',
    marginBottom: '16px',
  },
  formTitle: {
    fontSize: '2rem',
    color: '#1f2937',
    marginBottom: '8px',
    fontWeight: '700',
  },
  formSubtitle: {
    fontSize: '1rem',
    color: '#6b7280',
    marginBottom: '32px',
  },
  form: {
    marginBottom: '24px',
  },
  formGroup: {
    marginBottom: '24px',
  },
  label: {
    display: 'block',
    marginBottom: '8px',
    color: '#374151',
    fontSize: '0.9rem',
    fontWeight: '600',
  },
  input: {
    width: '100%',
    padding: '16px',
    border: '2px solid #e5e7eb',
    borderRadius: '12px',
