import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('Testing connection...');
  const [backendStatus, setBackendStatus] = useState('Checking...');

  // Use the actual backend URL (port 5000) instead of proxy
  const BACKEND_URL = 'http://localhost:5000';

  useEffect(() => {
    // Test if backend is connected - use full URL
    axios.get(`${BACKEND_URL}/api/auth/test-db`)
      .then(response => {
        setMessage('✅ Backend Connected Successfully!');
        setBackendStatus('✅ Connected');
        console.log('Backend response:', response.data);
      })
      .catch(error => {
        setMessage('❌ Backend Connection Failed');
        setBackendStatus('❌ Not Connected');
        console.log('Connection error:', error.message);
      });
  }, []);

  const testRegister = async () => {
    try {
      const response = await axios.post(`${BACKEND_URL}/api/auth/register`, {
        name: 'Test User',
        email: 'test@hrms.com',
        password: 'password123',
        role: 'Employee'
      });
      alert('✅ Registration successful!');
      console.log('Registration response:', response.data);
      
      // Also test login with the same user
      const loginResponse = await axios.post(`${BACKEND_URL}/api/auth/login`, {
        email: 'test@hrms.com',
        password: 'password123'
      });
      alert('✅ Login also successful!');
      console.log('Login response:', loginResponse.data);
      
    } catch (error) {
      alert('❌ Registration failed. Check browser console.');
      console.log('Error:', error.response?.data);
    }
  };

  const testLogin = async () => {
    try {
      const response = await axios.post(`${BACKEND_URL}/api/auth/login`, {
        email: 'test@hrms.com',
        password: 'password123'
      });
      alert('✅ Login successful!');
      console.log('Login response:', response.data);
    } catch (error) {
      alert('❌ Login failed. Make sure test user exists.');
      console.log('Login error:', error.response?.data);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '20px',
      fontFamily: 'Arial, sans-serif',
      color: 'white'
    }}>
      <div style={{
        maxWidth: '800px',
        margin: '0 auto',
        textAlign: 'center'
      }}>
        <h1 style={{ fontSize: '2.5rem', marginBottom: '10px' }}>
          HRMS Lite - Backend Connection Test
        </h1>
        
        <div style={{
          background: 'rgba(255, 255, 255, 0.1)',
          padding: '20px',
          borderRadius: '10px',
          marginBottom: '20px'
        }}>
          <h2>Status</h2>
          <p style={{ fontSize: '1.5rem' }}>{message}</p>
          <p><strong>Backend URL:</strong> {BACKEND_URL}</p>
        </div>

        <div style={{
          display: 'flex',
          gap: '15px',
          justifyContent: 'center',
          marginBottom: '30px',
          flexWrap: 'wrap'
        }}>
          <button 
            onClick={testRegister}
            style={{
              padding: '15px 25px',
              fontSize: '1rem',
              backgroundColor: '#28a745',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer'
            }}
          >
            Test Registration
          </button>

          <button 
            onClick={testLogin}
            style={{
              padding: '15px 25px',
              fontSize: '1rem',
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer'
            }}
          >
            Test Login
          </button>
        </div>

        <div style={{
          background: 'rgba(255, 255, 255, 0.1)',
          padding: '20px',
          borderRadius: '10px',
          textAlign: 'left'
        }}>
          <h3>What to do:</h3>
          <ol style={{ textAlign: 'left', marginLeft: '20px', lineHeight: '1.6' }}>
            <li>Click <strong>"Test Registration"</strong> to create a test user</li>
            <li>If successful, click <strong>"Test Login"</strong> to test login</li>
            <li>Check browser console (F12) for detailed results</li>
            <li>Tell me what you see in the console</li>
          </ol>
        </div>

        <div style={{
          marginTop: '30px',
          fontSize: '0.9rem',
          opacity: '0.8'
        }}>
          <p><strong>Backend:</strong> http://localhost:5000</p>
          <p><strong>Frontend:</strong> http://localhost:3000</p>
          <p><strong>API Docs:</strong> http://localhost:5000/api-docs</p>
        </div>
      </div>
    </div>
  );
}

export default App;
