import { useState } from 'react';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [focusedInput, setFocusedInput] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Simulated login - replace with actual login logic
    setTimeout(() => {
      setLoading(false);
      // Add your login logic here
    }, 2000);
  };

  return (
    <div style={styles.container}>
      {/* Animated background elements */}
      <div style={styles.bgCircle1}></div>
      <div style={styles.bgCircle2}></div>
      <div style={styles.bgCircle3}></div>

      <div style={styles.card}>
        {/* Decorative top bar */}
        <div style={styles.decorativeBar}></div>

        {/* Header Section */}
        <div style={styles.header}>
          <div style={styles.logoContainer}>
            <div style={styles.logoIcon}>
              <span style={styles.logoText}>HR</span>
              <div style={styles.logoGlow}></div>
            </div>
          </div>
          <h2 style={styles.title}>Welcome Back</h2>
          <p style={styles.subtitle}>Sign in to your HRMS account</p>
        </div>

        {/* Error Message */}
        {error && (
          <div style={styles.error}>
            <span style={styles.errorIcon}>âš </span>
            <span>{error}</span>
          </div>
        )}

        {/* Form Section */}
        <div style={styles.form}>
          <div style={styles.formGroup}>
            <label style={styles.label}>
              <span style={styles.labelIcon}>âœ‰</span>
              Email Address
            </label>
            <div style={styles.inputWrapper}>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                onFocus={() => setFocusedInput('email')}
                onBlur={() => setFocusedInput('')}
                required
                style={{
                  ...styles.input,
                  ...(focusedInput === 'email' ? styles.inputFocused : {})
                }}
                placeholder="Enter your email"
              />
              <div style={{
                ...styles.inputUnderline,
                ...(focusedInput === 'email' ? styles.inputUnderlineActive : {})
              }}></div>
            </div>
          </div>

          <div style={styles.formGroup}>
            <div style={styles.labelContainer}>
              <label style={styles.label}>
                <span style={styles.labelIcon}>ðŸ”’</span>
                Password
              </label>
              <a href="/forgot-password" style={styles.forgotLink}>
                Forgot password?
              </a>
            </div>
            <div style={styles.inputWrapper}>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                onFocus={() => setFocusedInput('password')}
                onBlur={() => setFocusedInput('')}
                required
                style={{
                  ...styles.input,
                  ...(focusedInput === 'password' ? styles.inputFocused : {})
                }}
                placeholder="Enter your password"
              />
              <div style={{
                ...styles.inputUnderline,
                ...(focusedInput === 'password' ? styles.inputUnderlineActive : {})
              }}></div>
            </div>
          </div>

          <button 
            onClick={handleSubmit}
            style={{
              ...styles.button,
              ...(loading ? styles.buttonDisabled : {})
            }}
            disabled={loading}
          >
            {loading ? (
              <div style={styles.buttonContent}>
                <div style={styles.spinner}></div>
                <span>Signing in...</span>
              </div>
            ) : (
              <>
                <span>Sign In</span>
                <span style={styles.buttonArrow}>â†’</span>
              </>
            )}
            <div style={styles.buttonShine}></div>
          </button>
        </div>

        {/* Footer Section */}
        <div style={styles.footer}>
          <div style={styles.divider}>
            <span style={styles.dividerText}>or</span>
          </div>
          <p style={styles.footerText}>
            Don't have an account?{' '}
            <a href="/register" style={styles.registerLink}>
              Create account
            </a>
          </p>
        </div>
      </div>

      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
        
        @keyframes float {
          0%, 100% { transform: translateY(0px) scale(1); }
          50% { transform: translateY(-20px) scale(1.05); }
        }
        
        @keyframes pulse {
          0%, 100% { opacity: 0.6; transform: scale(1); }
          50% { opacity: 0.8; transform: scale(1.05); }
        }
        
        @keyframes shine {
          0% { left: -100%; }
          100% { left: 200%; }
        }
        
        @keyframes slideIn {
          0% { opacity: 0; transform: translateY(20px); }
          100% { opacity: 1; transform: translateY(0); }
        }
      `}</style>
    </div>
  );
};

