import { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    role: 'Employee',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [selectedPortal, setSelectedPortal] = useState(null); // Track which portal is selected

  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handlePortalSelect = (portalType) => {
    setSelectedPortal(portalType);
    setFormData({
      ...formData,
      role: portalType === 'employee' ? 'Employee' : 'Admin'
    });
    setError(''); // Clear any previous errors
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedPortal) {
      setError('Please select a portal type');
      return;
    }

    setError('');
    setLoading(true);

    const result = await login(formData.email, formData.password);

    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.message);
    }

    setLoading(false);
  };

  const handleBackToSelection = () => {
    setSelectedPortal(null);
    setFormData({ email: '', password: '', role: 'Employee' });
    setError('');
  };

  // If a portal is selected, show the login form for that portal
  if (selectedPortal) {
    return (
      <div style={styles.container}>
        <div style={styles.formCard}>
          {/* Back Button */}
          <button onClick={handleBackToSelection} style={styles.backButton}>
            ‚Üê Back to Portal Selection
          </button>

          {/* Header */}
          <div style={styles.formHeader}>
            <div style={styles.portalIcon}>
              {selectedPortal === 'employee' ? 'üë®‚Äçüíº' : 'üëë'}
            </div>
            <h2 style={styles.formTitle}>
              {selectedPortal === 'employee' ? 'Employee Portal' : 'Admin Portal'}
            </h2>
            <p style={styles.formSubtitle}>
              Sign in to access your {selectedPortal === 'employee' ? 'employee' : 'administrative'} dashboard
            </p>
          </div>

          {error && (
            <div style={styles.error}>
              <span style={styles.errorIcon}>‚ö†Ô∏è</span>
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit} style={styles.form}>
            <div style={styles.formGroup}>
              <label style={styles.label}>Email Address</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                style={styles.input}
                placeholder={`Enter your ${selectedPortal === 'employee' ? 'employee' : 'admin'} email`}
              />
            </div>

            <div style={styles.formGroup}>
              <label style={styles.label}>Password</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
                style={styles.input}
                placeholder="Enter your password"
              />
            </div>

            <div style={styles.formOptions}>
              <label style={styles.remember}>
                <input type="checkbox" style={styles.checkbox} />
                Remember me
              </label>
              <Link to="/forgot-password" style={styles.forgotLink}>
                Forgot password?
              </Link>
            </div>

            <button 
              type="submit" 
              style={{
                ...styles.button,
                ...(loading ? styles.buttonLoading : {})
              }}
              disabled={loading}
            >
              {loading ? (
                <>
                  <span style={styles.spinner}></span>
                  Signing In...
                </>
              ) : (
                `Sign In to ${selectedPortal === 'employee' ? 'Employee Portal' : 'Admin Portal'}`
              )}
            </button>
          </form>

          <div style={styles.securityNote}>
            <span style={styles.securityIcon}>üîí</span>
            Secure {selectedPortal === 'employee' ? 'employee' : 'administrative'} access
          </div>
        </div>
      </div>
    );
  }

  // Show portal selection (3 cards)
  return (
    <div style={styles.container}>
      {/* Header Section */}
      <div style={styles.header}>
        <div style={styles.logo}>
          <div style={styles.logoIcon}>üíº</div>
          <h1 style={styles.title}>HRMS Pro</h1>
        </div>
        <p style={styles.subtitle}>Choose your portal to continue</p>
      </div>

      {/* Three Cards Section */}
      <div style={styles.cardsContainer}>
        {/* Employee Portal Card */}
        <div 
          style={styles.card}
          onClick={() => handlePortalSelect('employee')}
        >
          <div style={styles.cardIcon}>üë®‚Äçüíº</div>
          <h3 style={styles.cardTitle}>Employee Portal</h3>
          <p style={styles.cardDescription}>
            Access your personal dashboard, view schedules, and manage your profile
          </p>
          <div style={styles.cardFeatures}>
            <span style={styles.feature}>View Schedule</span>
            <span style={styles.feature}>Request Time Off</span>
            <span style={styles.feature}>Update Profile</span>
          </div>
          <div style={styles.cardAction}>
            Click to Sign In <span style={styles.arrow}>‚Üí</span>
          </div>
          <div style={styles.cardBadge}>For Team Members</div>
        </div>

        {/* Admin Portal Card */}
        <div 
          style={styles.card}
          onClick={() => handlePortalSelect('admin')}
        >
          <div style={styles.cardIcon}>üëë</div>
          <h3 style={styles.cardTitle}>Admin Portal</h3>
          <p style={styles.cardDescription}>
            Manage employees, departments, and access advanced administrative features
          </p>
          <div style={styles.cardFeatures}>
            <span style={styles.feature}>Employee Management</span>
            <span style={styles.feature}>Department Settings</span>
            <span style={styles.feature}>Advanced Analytics</span>
          </div>
          <div style={styles.cardAction}>
            Click to Sign In <span style={styles.arrow}>‚Üí</span>
          </div>
          <div style={styles.cardBadgeAdmin}>For Administrators</div>
        </div>

        {/* Register Card */}
        <Link to="/register" style={styles.cardLink}>
          <div style={styles.cardRegister}>
            <div style={styles.cardIcon}>üöÄ</div>
            <h3 style={styles.cardTitle}>Get Started</h3>
            <p style={styles.cardDescription}>
              Create a new account and join your organization's HRMS platform
            </p>
            <div style={styles.cardFeatures}>
              <span style={styles.feature}>Quick Setup</span>
              <span style={styles.feature}>Secure Access</span>
              <span style={styles.feature}>24/7 Support</span>
            </div>
            <div style={styles.registerCta}>
              Create Account <span style={styles.arrow}>‚Üí</span>
            </div>
          </div>
        </Link>
      </div>

      {/* Footer Note */}
      <div style={styles.footerNote}>
        <p>Select your portal type to proceed with authentication</p>
      </div>
    </div>
  );
};

