import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [selectedRole, setSelectedRole] = useState(null);

  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!selectedRole) {
      setError('Please select a role first');
      return;
    }

    setError('');
    setLoading(true);

    // Pass the selected role to login function
    const result = await login(formData.email, formData.password, selectedRole);

    if (result.success) {
      // Redirect based on actual user role from backend
      if (selectedRole === 'admin') {
        navigate('/admin-dashboard');
      } else {
        navigate('/employee-dashboard');
      }
    } else {
      setError(result.message);
    }

    setLoading(false);
  };

  const handleRoleSelect = (role) => {
    setSelectedRole(role);
    setError('');
    setFormData({ email: '', password: '' });
  };

  return (
    <div style={styles.container}>
      <div style={styles.content}>
        <div style={styles.header}>
          <h1 style={styles.title}>HRMS Lite</h1>
          <p style={styles.subtitle}>Employee Management System</p>
        </div>

        {!selectedRole ? (
          <div style={styles.rolesContainer}>
            <div 
              style={styles.roleCard}
              onClick={() => handleRoleSelect('admin')}
            >
              <div style={styles.roleIconContainer}>
                <div style={{...styles.roleIcon, background: 'linear-gradient(135deg, #dc2626 0%, #b91c1c 100%)'}}>
                  üîí
                </div>
              </div>
              <h3 style={styles.roleTitle}>Admin Login</h3>
              <p style={styles.roleDescription}>
                Strictly for administrators only. Employee access will be denied.
              </p>
              <div style={styles.accessWarning}>
                <span style={styles.warningText}>‚ö†Ô∏è Employees cannot access this portal</span>
              </div>
              <button 
                type="button"
                style={{...styles.roleButton, background: 'linear-gradient(135deg, #dc2626 0%, #b91c1c 100%)'}}>
                Continue as Admin
              </button>
            </div>

            <div 
              style={styles.roleCard}
              onClick={() => handleRoleSelect('employee')}
            >
              <div style={styles.roleIconContainer}>
                <div style={{...styles.roleIcon, background: 'linear-gradient(135deg, #2563eb 0%, #1d4ed8 100%)'}}>
                  üë§
                </div>
              </div>
              <h3 style={styles.roleTitle}>Employee Login</h3>
              <p style={styles.roleDescription}>
                For employees only. Admin access will be denied from this portal.
              </p>
              <div style={styles.accessWarning}>
                <span style={styles.warningText}>‚ö†Ô∏è Admins cannot access this portal</span>
              </div>
              <button 
                type="button"
                style={{...styles.roleButton, background: 'linear-gradient(135deg, #2563eb 0%, #1d4ed8 100%)'}}>
                Continue as Employee
              </button>
            </div>
          </div>
        ) : (
          <div style={styles.formContainer}>
            <button 
              type="button"
              style={styles.backButton}
              onClick={() => {
                setSelectedRole(null);
                setError('');
                setFormData({ email: '', password: '' });
              }}
            >
              ‚Üê Back to Role Selection
            </button>

            <div style={styles.formCard}>
              <div style={styles.formHeader}>
                <div style={{
                  ...styles.formIcon, 
                  background: selectedRole === 'admin' 
                    ? 'linear-gradient(135deg, #dc2626 0%, #b91c1c 100%)' 
                    : 'linear-gradient(135deg, #2563eb 0%, #1d4ed8 100%)'
                }}>
                  {selectedRole === 'admin' ? 'üîí' : 'üë§'}
                </div>
                <h2 style={styles.formTitle}>
                  {selectedRole === 'admin' ? 'Admin' : 'Employee'} Portal
                </h2>
                <p style={styles.formSubtitle}>
                  {selectedRole === 'admin' 
                    ? 'Strictly for administrators only' 
                    : 'For employees only'
                  }
                </p>
                <div style={styles.roleWarning}>
                  {selectedRole === 'admin' 
                    ? '‚ö†Ô∏è Employee access will be rejected' 
                    : '‚ö†Ô∏è Admin access will be rejected'
                  }
                </div>
              </div>

              {error && (
                <div style={{
                  ...styles.error,
                  background: error.includes('Access denied') ? '#fef2f2' : '#fef2f2',
                  border: error.includes('Access denied') ? '1px solid #fecaca' : '1px solid #fecaca'
                }}>
                  {error}
                </div>
              )}

              <form onSubmit={handleSubmit} style={styles.form}>
                <div style={styles.formGroup}>
                  <label style={styles.label}>Email Address</label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    style={styles.input}
                    placeholder="Enter your email"
                  />
                </div>

                <div style={styles.formGroup}>
                  <label style={styles.label}>Password</label>
                  <input
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                    style={styles.input}
                    placeholder="Enter your password"
                  />
                </div>

                <input type="hidden" name="role" value={selectedRole} />

                <button 
                  type="submit" 
                  style={{
                    ...styles.submitButton,
                    background: selectedRole === 'admin'
                      ? 'linear-gradient(135deg, #dc2626 0%, #b91c1c 100%)'
                      : 'linear-gradient(135deg, #2563eb 0%, #1d4ed8 100%)'
                  }}
                  disabled={loading}
                >
                  {loading ? 'Verifying Access...' : `Sign In as ${selectedRole === 'admin' ? 'Admin' : 'Employee'}`}
                </button>
              </form>

              <p style={styles.link}>
                Don't have an account? <Link to="/register" style={styles.linkText}>Create one here</Link>
              </p>
            </div>
          </div>
        )}

        <div style={styles.footer}>
          <div style={styles.feature}>
            <span style={styles.featureIcon}>üîê</span>
            <span>Strict Role Access</span>
          </div>
          <div style={styles.feature}>
            <span style={styles.featureIcon}>üö´</span>
            <span>Cross-Role Access Denied</span>
          </div>
          <div style={styles.feature}>
            <span style={styles.featureIcon}>‚ö°</span>
            <span>Secure & Reliable</span>
          </div>
        </div>
      </div>
    </div>
  );
};

