import { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const AuthPage = () => {
  const [selectedCard, setSelectedCard] = useState(null); // 'login', 'register', or null
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    role: 'Employee'
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const { login, register } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const result = await login(formData.email, formData.password);

    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.message);
    }

    setLoading(false);
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const result = await register(formData);

    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.message);
    }

    setLoading(false);
  };

  const handleBackToSelection = () => {
    setSelectedCard(null);
    setFormData({
      email: '',
      password: '',
      name: '',
      role: 'Employee'
    });
    setError('');
  };

  // If a card is selected, show the form for that card
  if (selectedCard) {
    return (
      <div style={styles.container}>
        <div style={styles.formCard}>
          {/* Back Button */}
          <button onClick={handleBackToSelection} style={styles.backButton}>
            ‚Üê Back to Selection
          </button>

          {/* Form Header */}
          <div style={styles.formHeader}>
            <div style={styles.formIcon}>
              {selectedCard === 'login' ? 'üîê' : 'üöÄ'}
            </div>
            <h2 style={styles.formTitle}>
              {selectedCard === 'login' ? 'Sign In to HRMS' : 'Create HRMS Account'}
            </h2>
            <p style={styles.formSubtitle}>
              {selectedCard === 'login' 
                ? 'Enter your credentials to access your account' 
                : 'Fill in your details to create a new account'
              }
            </p>
          </div>

          {error && (
            <div style={styles.error}>
              <span style={styles.errorIcon}>‚ö†Ô∏è</span>
              {error}
            </div>
          )}

          <form onSubmit={selectedCard === 'login' ? handleLogin : handleRegister} style={styles.form}>
            {selectedCard === 'register' && (
              <div style={styles.formGroup}>
                <label style={styles.label}>Full Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  style={styles.input}
                  placeholder="Enter your full name"
                />
              </div>
            )}

            <div style={styles.formGroup}>
              <label style={styles.label}>Email Address</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                style={styles.input}
                placeholder={selectedCard === 'login' ? "Enter your email" : "Enter your work email"}
              />
            </div>

            <div style={styles.formGroup}>
              <label style={styles.label}>Password</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
                minLength={selectedCard === 'register' ? "6" : undefined}
                style={styles.input}
                placeholder={
                  selectedCard === 'login' 
                    ? "Enter your password" 
                    : "Create a password (min 6 characters)"
                }
              />
            </div>

            {selectedCard === 'register' && (
              <div style={styles.formGroup}>
                <label style={styles.label}>Account Type</label>
                <select
                  name="role"
                  value={formData.role}
                  onChange={handleChange}
                  style={styles.select}
                >
                  <option value="Employee">üë®‚Äçüíº Employee Account</option>
                  <option value="Admin">üëë Administrator Account</option>
                </select>
                <p style={styles.roleHint}>
                  {formData.role === 'Employee' 
                    ? 'Standard access for team members' 
                    : 'Full administrative privileges'
                  }
                </p>
              </div>
            )}

            {selectedCard === 'login' && (
              <div style={styles.formOptions}>
                <label style={styles.remember}>
                  <input type="checkbox" style={styles.checkbox} />
                  Remember me
                </label>
                <a href="/forgot-password" style={styles.forgotLink}>
                  Forgot password?
                </a>
              </div>
            )}

            <button 
              type="submit" 
              style={{
                ...styles.button,
                ...(loading ? styles.buttonLoading : {})
              }}
              disabled={loading}
            >
              {loading ? (
                <>
                  <span style={styles.spinner}></span>
                  {selectedCard === 'login' ? 'Signing In...' : 'Creating Account...'}
                </>
              ) : (
                selectedCard === 'login' ? 'Sign In' : 'Create Account'
              )}
            </button>
          </form>

          <div style={styles.securityNote}>
            <span style={styles.securityIcon}>üîí</span>
            Your data is securely encrypted and protected
          </div>
        </div>
      </div>
    );
  }

  // Show card selection (initial view)
  return (
    <div style={styles.container}>
      {/* Header Section */}
      <div style={styles.header}>
        <div style={styles.logo}>
          <div style={styles.logoIcon}>üíº</div>
          <h1 style={styles.title}>HRMS Pro</h1>
        </div>
        <p style={styles.subtitle}>Choose your authentication method</p>
      </div>

      {/* Two Cards Section */}
      <div style={styles.cardsContainer}>
        {/* Login Card */}
        <div 
          style={styles.card}
          onClick={() => setSelectedCard('login')}
        >
          <div style={styles.cardIcon}>üîê</div>
          <h3 style={styles.cardTitle}>Sign In</h3>
          <p style={styles.cardDescription}>
            Access your existing HRMS account with your credentials
          </p>
          <div style={styles.cardFeatures}>
            <span style={styles.feature}>Employee & Admin Access</span>
            <span style={styles.feature}>Secure Authentication</span>
            <span style={styles.feature}>Quick Access</span>
          </div>
          <div style={styles.cardAction}>
            Click to Sign In <span style={styles.arrow}>‚Üí</span>
          </div>
          <div style={styles.cardBadge}>Existing Users</div>
        </div>

        {/* Register Card */}
        <div 
          style={styles.card}
          onClick={() => setSelectedCard('register')}
        >
          <div style={styles.cardIcon}>üöÄ</div>
          <h3 style={styles.cardTitle}>Create Account</h3>
          <p style={styles.cardDescription}>
            Join your organization's HRMS platform with a new account
          </p>
          <div style={styles.cardFeatures}>
            <span style={styles.feature}>Employee/Admin Roles</span>
            <span style={styles.feature}>Quick Setup</span>
            <span style={styles.feature}>24/7 Support</span>
          </div>
          <div style={styles.cardAction}>
            Click to Register <span style={styles.arrow}>‚Üí</span>
          </div>
          <div style={styles.cardBadgeRegister}>New Users</div>
        </div>
      </div>

      {/* Footer Note */}
      <div style={styles.footerNote}>
        <p>Select an option above to get started</p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
