import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [selectedRole, setSelectedRole] = useState(null);

  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const result = await login(formData.email, formData.password);

    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.message);
    }

    setLoading(false);
  };

  const handleRoleSelect = (role) => {
    setSelectedRole(role);
    setError('');
  };

  return (
    <div style={styles.container}>
      <div style={styles.content}>
        <div style={styles.header}>
          <h1 style={styles.title}>HRMS Lite</h1>
          <p style={styles.subtitle}>Employee Management System</p>
        </div>

        {!selectedRole ? (
          <div style={styles.rolesContainer}>
            <div 
              style={styles.roleCard}
              onClick={() => handleRoleSelect('admin')}
            >
              <div style={styles.roleIconContainer}>
                <div style={{...styles.roleIcon, background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'}}>
                  üë®‚Äçüíº
                </div>
              </div>
              <h3 style={styles.roleTitle}>Admin Login</h3>
              <p style={styles.roleDescription}>
                Full system access with administrative privileges
              </p>
              <div style={styles.roleBadge}>
                <span style={styles.badge}>üîê Secure</span>
                <span style={styles.badge}>‚öôÔ∏è Manage</span>
              </div>
              <button 
                type="button"
                style={{...styles.roleButton, background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'}}>
                Continue as Admin
              </button>
            </div>

            <div 
              style={styles.roleCard}
              onClick={() => handleRoleSelect('employee')}
            >
              <div style={styles.roleIconContainer}>
                <div style={{...styles.roleIcon, background: 'linear-gradient(135deg, #00c6ff 0%, #0072ff 100%)'}}>
                  üë§
                </div>
              </div>
              <h3 style={styles.roleTitle}>Employee Login</h3>
