              </div>
              <button 
                type="button"
                style={{...styles.roleButton, background: 'linear-gradient(135deg, #00c6ff 0%, #0072ff 100%)'}}>
                Continue as Employee
              </button>
            </div>
          </div>
        ) : (
          <div style={styles.formContainer}>
            <button 
              type="button"
              style={styles.backButton}
              onClick={() => {
                setSelectedRole(null);
                setError('');
              }}
            >
              ‚Üê Back to Role Selection
            </button>

            <div style={styles.formCard}>
              <div style={styles.formHeader}>
                <div style={{
                  ...styles.formIcon, 
                  background: selectedRole === 'admin' 
                    ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' 
                    : 'linear-gradient(135deg, #00c6ff 0%, #0072ff 100%)'
                }}>
                  {selectedRole === 'admin' ? 'üë®‚Äçüíº' : 'üë§'}
                </div>
                <h2 style={styles.formTitle}>
                  {selectedRole === 'admin' ? 'Admin' : 'Employee'} Login
                </h2>
                <p style={styles.formSubtitle}>Enter your credentials to continue</p>
              </div>

              {error && <div style={styles.error}>{error}</div>}

              <form onSubmit={handleSubmit} style={styles.form}>
                <div style={styles.formGroup}>
                  <label style={styles.label}>Email Address</label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    style={styles.input}
                    placeholder="Enter your email"
                  />
                </div>

                <div style={styles.formGroup}>
                  <label style={styles.label}>Password</label>
                  <input
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                    style={styles.input}
                    placeholder="Enter your password"
                  />
                </div>

                <button 
                  type="submit" 
                  style={{
                    ...styles.submitButton,
                    background: selectedRole === 'admin'
                      ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
                      : 'linear-gradient(135deg, #00c6ff 0%, #0072ff 100%)'
                  }}
                  disabled={loading}
                >
                  {loading ? 'Signing In...' : 'Sign In'}
                </button>
              </form>

              <p style={styles.link}>
                Don't have an account? <Link to="/register" style={styles.linkText}>Create one here</Link>
              </p>
            </div>
          </div>
        )}

        <div style={styles.footer}>
          <div style={styles.feature}>
            <span style={styles.featureIcon}>üîê</span>
