import { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const result = await login(formData.email, formData.password);

    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.message);
    }

    setLoading(false);
  };

  return (
    <div style={styles.container}>
      {/* Header Section */}
      <div style={styles.header}>
        <div style={styles.logo}>
          <div style={styles.logoIcon}>üíº</div>
          <h1 style={styles.title}>HRMS Pro</h1>
        </div>
        <p style={styles.subtitle}>Welcome back! Please sign in to continue</p>
      </div>

      {/* Two Cards Section */}
      <div style={styles.cardsContainer}>
        {/* Login Card */}
        <div style={styles.loginCard}>
          <div style={styles.cardHeader}>
            <div style={styles.cardIcon}>üîê</div>
            <h2 style={styles.cardTitle}>Sign In</h2>
            <p style={styles.cardSubtitle}>Access your HRMS account</p>
          </div>

          {error && (
            <div style={styles.error}>
              <span style={styles.errorIcon}>‚ö†Ô∏è</span>
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit} style={styles.form}>
            <div style={styles.formGroup}>
              <label style={styles.label}>Email Address</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                style={styles.input}
                placeholder="Enter your email address"
              />
            </div>

            <div style={styles.formGroup}>
              <label style={styles.label}>Password</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
