import React, { useState, useRef, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import ThemeToggle from './ThemeToggle';

const Navbar = () => {
  const { user, logout } = useAuth();
  const location = useLocation();
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const profileMenuRef = useRef(null);

  const isActive = (path) => location.pathname === path;

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (profileMenuRef.current && !profileMenuRef.current.contains(event.target)) {
        setShowProfileMenu(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <nav style={styles.navbar}>
      <div style={styles.container}>
        <Link to="/dashboard" style={styles.logo}>
          <span style={styles.logoIcon}>üè¢</span>
          EMS Lite
        </Link>
        
        <div style={styles.navLinks}>
          <Link 
            to="/dashboard" 
            style={{
              ...styles.navLink,
              ...(isActive('/dashboard') ? styles.activeLink : {})
            }}
          >
            <span style={styles.navIcon}>üìä</span>
            Dashboard
          </Link>
          
          {user?.role === 'Admin' && (
            <>
              <Link 
                to="/employees" 
                style={{
                  ...styles.navLink,
                  ...(isActive('/employees') ? styles.activeLink : {})
                }}
              >
                <span style={styles.navIcon}>üë•</span>
                Employees
              </Link>
              <Link 
                to="/departments" 
                style={{
                  ...styles.navLink,
                  ...(isActive('/departments') ? styles.activeLink : {})
                }}
              >
                <span style={styles.navIcon}>üè¢</span>
                Departments
              </Link>
            </>
          )}
          
          <Link 
            to="/attendance" 
            style={{
              ...styles.navLink,
              ...(isActive('/attendance') ? styles.activeLink : {})
            }}
          >
            <span style={styles.navIcon}>üìÖ</span>
            Attendance
          </Link>
        </div>

        <div style={styles.userSection} ref={profileMenuRef}>
          <ThemeToggle />
          <button 
            onClick={() => setShowProfileMenu(!showProfileMenu)} 
            style={styles.profileButton}
          >
            <div style={styles.profileIcon}>
              {user?.name?.charAt(0)?.toUpperCase() || '?'}
            </div>
          </button>
          
          {showProfileMenu && (
            <div style={styles.profileMenu}>
              <div style={styles.profileHeader}>
                <div style={styles.profileAvatar}>
                  {user?.name?.charAt(0)?.toUpperCase() || '?'}
                </div>
                <div style={styles.profileInfo}>
                  <span style={styles.profileName}>{user?.name}</span>
                  <span style={styles.profileRole}>{user?.role}</span>
                </div>
              </div>
              <div style={styles.menuDivider}></div>
              <button onClick={logout} style={styles.menuLogoutBtn}>
                <span style={styles.logoutIcon}>üö™</span>
                Logout
              </button>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

const styles = {
  navbar: {
    background: 'rgba(255, 255, 255, 0.95)',
    backdropFilter: 'blur(10px)',
    padding: '1rem 0',
    boxShadow: '0 2px 20px rgba(0, 0, 0, 0.1)',
    borderBottom: '1px solid rgba(255, 255, 255, 0.2)',
  },
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '0 20px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  logo: {
    display: 'flex',
    alignItems: 'center',
    gap: '10px',
    color: '#333',
    fontSize: '1.5rem',
    fontWeight: 'bold',
    textDecoration: 'none',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
  },
  logoIcon: {
    fontSize: '1.8rem',
  },
  navLinks: {
    display: 'flex',
    gap: '10px',
    alignItems: 'center',
  },
  navLink: {
    display: 'flex',
    alignItems: 'center',
    gap: '8px',
    color: '#666',
    textDecoration: 'none',
    padding: '10px 20px',
    borderRadius: '12px',
    transition: 'all 0.3s ease',
    fontSize: '1rem',
    fontWeight: '500',
  },
  activeLink: {
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    color: 'white',
  },
  navIcon: {
    fontSize: '1.2rem',
  },
  userSection: {
    display: 'flex',
    alignItems: 'center',
    gap: '20px',
  },
  userInfo: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-end',
  },
  userName: {
    fontSize: '1rem',
    fontWeight: '600',
    color: '#333',
  },
  userRole: {
    fontSize: '0.8rem',
    color: '#667eea',
    fontWeight: '500',
  },
  logoutBtn: {
    display: 'flex',
    alignItems: 'center',
    gap: '8px',
    padding: '10px 20px',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    color: 'white',
    border: 'none',
    borderRadius: '12px',
    cursor: 'pointer',
    fontSize: '0.9rem',
    fontWeight: '500',
    transition: 'all 0.3s ease',
  },
  logoutIcon: {
    fontSize: '1.1rem',
  },
  profileButton: {
    width: '40px',
    height: '40px',
    padding: 0,
    border: 'none',
    borderRadius: '50%',
    cursor: 'pointer',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    position: 'relative',
  },
  profileIcon: {
    color: 'white',
    fontSize: '1.2rem',
    fontWeight: 'bold',
  },
  profileMenu: {
    position: 'absolute',
    top: '60px',
    right: '0',
    width: '250px',
    background: 'white',
    borderRadius: '12px',
    boxShadow: '0 4px 20px rgba(0, 0, 0, 0.15)',
    padding: '1rem',
    zIndex: 1000,
  },
  profileHeader: {
    display: 'flex',
    alignItems: 'center',
    gap: '12px',
    padding: '0.5rem',
  },
  profileAvatar: {
    width: '48px',
    height: '48px',
    borderRadius: '50%',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    color: 'white',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '1.5rem',
    fontWeight: 'bold',
  },
  profileInfo: {
    display: 'flex',
    flexDirection: 'column',
  },
  profileName: {
    fontSize: '1rem',
    fontWeight: '600',
    color: '#333',
  },
  profileRole: {
    fontSize: '0.85rem',
    color: '#667eea',
    fontWeight: '500',
  },
  menuDivider: {
    height: '1px',
    background: 'rgba(0, 0, 0, 0.1)',
    margin: '0.75rem 0',
  },
  menuLogoutBtn: {
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    gap: '8px',
    padding: '10px',
    background: 'transparent',
    color: '#333',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    fontSize: '0.9rem',
    fontWeight: '500',
    transition: 'all 0.3s ease',
    '&:hover': {
      background: 'rgba(102, 126, 234, 0.1)',
    },
  },
};

export default Navbar;
