import { useState } from 'react';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    role: 'Employee',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [focusedInput, setFocusedInput] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Simulated registration - replace with actual register logic
    setTimeout(() => {
      setLoading(false);
      // Add your registration logic here
    }, 2000);
  };

  return (
    <div style={styles.container}>
      {/* Animated background elements */}
      <div style={styles.bgCircle1}></div>
      <div style={styles.bgCircle2}></div>
      <div style={styles.bgCircle3}></div>

      <div style={styles.card}>
        {/* Decorative top bar */}
        <div style={styles.decorativeBar}></div>

        {/* Header Section */}
        <div style={styles.header}>
          <div style={styles.logoContainer}>
            <div style={styles.logoIcon}>
              <span style={styles.logoText}>HR</span>
              <div style={styles.logoGlow}></div>
            </div>
          </div>
          <h2 style={styles.title}>Create Account</h2>
          <p style={styles.subtitle}>Join HRMS to get started</p>
        </div>

        {/* Error Message */}
        {error && (
          <div style={styles.error}>
            <span style={styles.errorIcon}>‚ö†</span>
            <span>{error}</span>
          </div>
        )}

        {/* Form Section */}
        <div style={styles.form}>
          <div style={styles.formGroup}>
            <label style={styles.label}>
              <span style={styles.labelIcon}>üë§</span>
              Full Name
            </label>
            <div style={styles.inputWrapper}>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                onFocus={() => setFocusedInput('name')}
                onBlur={() => setFocusedInput('')}
                required
                style={{
                  ...styles.input,
                  ...(focusedInput === 'name' ? styles.inputFocused : {})
                }}
                placeholder="Enter your full name"
              />
              <div style={{
                ...styles.inputUnderline,
                ...(focusedInput === 'name' ? styles.inputUnderlineActive : {})
              }}></div>
            </div>
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>
              <span style={styles.labelIcon}>‚úâ</span>
              Email Address
            </label>
            <div style={styles.inputWrapper}>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                onFocus={() => setFocusedInput('email')}
                onBlur={() => setFocusedInput('')}
                required
                style={{
                  ...styles.input,
                  ...(focusedInput === 'email' ? styles.inputFocused : {})
                }}
                placeholder="Enter your email"
              />
              <div style={{
                ...styles.inputUnderline,
                ...(focusedInput === 'email' ? styles.inputUnderlineActive : {})
              }}></div>
            </div>
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>
              <span style={styles.labelIcon}>üîí</span>
              Password
            </label>
            <div style={styles.inputWrapper}>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                onFocus={() => setFocusedInput('password')}
                onBlur={() => setFocusedInput('')}
                required
                minLength="6"
                style={{
                  ...styles.input,
                  ...(focusedInput === 'password' ? styles.inputFocused : {})
                }}
                placeholder="Create a password (min 6 characters)"
              />
              <div style={{
                ...styles.inputUnderline,
                ...(focusedInput === 'password' ? styles.inputUnderlineActive : {})
              }}></div>
            </div>
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>
              <span style={styles.labelIcon}>üè¢</span>
              Account Type
            </label>
            <div style={styles.inputWrapper}>
              <select
                name="role"
                value={formData.role}
                onChange={handleChange}
                onFocus={() => setFocusedInput('role')}
                onBlur={() => setFocusedInput('')}
                style={{
                  ...styles.select,
                  ...(focusedInput === 'role' ? styles.selectFocused : {})
                }}
              >
                <option value="Employee">Employee</option>
                <option value="Admin">Administrator</option>
              </select>
              <div style={{
                ...styles.inputUnderline,
                ...(focusedInput === 'role' ? styles.inputUnderlineActive : {})
              }}></div>
            </div>
          </div>

          <button 
            onClick={handleSubmit}
            style={{
              ...styles.button,
              ...(loading ? styles.buttonDisabled : {})
            }}
            disabled={loading}
          >
            {loading ? (
              <div style={styles.buttonContent}>
                <div style={styles.spinner}></div>
                <span>Creating Account...</span>
              </div>
            ) : (
              <>
                <span>Create Account</span>
                <span style={styles.buttonArrow}>‚Üí</span>
              </>
            )}
            <div style={styles.buttonShine}></div>
          </button>
        </div>

        {/* Footer Section */}
        <div style={styles.footer}>
          <div style={styles.divider}>
            <span style={styles.dividerText}>or</span>
          </div>
          <p style={styles.footerText}>
            Already have an account?{' '}
            <a href="/login" style={styles.registerLink}>
              Sign in
            </a>
          </p>
        </div>
      </div>

      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
        
        @keyframes float {
          0%, 100% { transform: translateY(0px) scale(1); }
          50% { transform: translateY(-20px) scale(1.05); }
        }
        
        @keyframes pulse {
          0%, 100% { opacity: 0.6; transform: scale(1); }
          50% { opacity: 0.8; transform: scale(1.05); }
        }
        
        @keyframes shine {
          0% { left: -100%; }
          100% { left: 200%; }
        }
        
        @keyframes slideIn {
          0% { opacity: 0; transform: translateY(20px); }
          100% { opacity: 1; transform: translateY(0); }
        }
      `}</style>
    </div>
  );
};

const styles = {
  container: {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '20px',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif',
