import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('Ready to test backend connection');
  const [lastRegisteredUser, setLastRegisteredUser] = useState(null);
  const BACKEND_URL = 'http://localhost:5000';

  const testWithNewUser = async () => {
    setMessage('Testing...');
    
    // Create a unique email each time
    const timestamp = new Date().getTime();
    const testEmail = `test${timestamp}@hrms.com`;
    const testPassword = 'password123';
    
    try {
      // Step 1: Register a new user
      const registerResponse = await axios.post(`${BACKEND_URL}/api/auth/register`, {
        name: 'Test User',
        email: testEmail,
        password: testPassword,
        role: 'Employee'
      });
      
      console.log('‚úÖ Registration successful:', registerResponse.data);
      
      // Save the user details for later use
      setLastRegisteredUser({
        email: testEmail,
        password: testPassword
      });
      
      // Step 2: Login with the new user
      const loginResponse = await axios.post(`${BACKEND_URL}/api/auth/login`, {
        email: testEmail,
        password: testPassword
      });
      
      console.log('‚úÖ Login successful:', loginResponse.data);
      
      setMessage('‚úÖ SUCCESS! Backend is working perfectly!');
      alert('üéâ Backend is working! Check console for details.');
      
    } catch (error) {
      console.log('‚ùå Error:', error.response?.data);
      setMessage('‚ùå Backend error - check console');
      alert('Error occurred. Check browser console (F12) for details.');
    }
  };

  const testExistingUser = async () => {
    if (!lastRegisteredUser) {
      alert('Please register a user first using "Test with NEW User"');
      return;
    }
    
    setMessage('Testing login with existing user...');
    
    try {
      // Try to login with the last registered user
      const loginResponse = await axios.post(`${BACKEND_URL}/api/auth/login`, {
        email: lastRegisteredUser.email,
        password: lastRegisteredUser.password
      });
      
      console.log('‚úÖ Login successful:', loginResponse.data);
      setMessage('‚úÖ Login works! Backend is connected.');
      alert('‚úÖ Login successful with existing user!');
      
    } catch (error) {
      console.log('‚ùå Login error:', error.response?.data);
      setMessage('‚ùå Login failed - check console');
      alert('Login failed. Check console for error details.');
    }
  };

  const createTestUser = async () => {
    setMessage('Creating test user...');
    
    try {
      const response = await axios.post(`${BACKEND_URL}/api/auth/register`, {
        name: 'Test User',
        email: 'test@hrms.com',
        password: 'password123',
        role: 'Employee'
      });
      
      console.log('‚úÖ Test user created:', response.data);
      setLastRegisteredUser({
        email: 'test@hrms.com',
        password: 'password123'
      });
      setMessage('‚úÖ Test user created: test@hrms.com');
      alert('‚úÖ Test user "test@hrms.com" created!');
      
    } catch (error) {
      if (error.response?.data?.message === 'User already exists') {
        setLastRegisteredUser({
          email: 'test@hrms.com',
          password: 'password123'
        });
        setMessage('‚úÖ Test user already exists: test@hrms.com');
        alert('‚úÖ Test user "test@hrms.com" already exists!');
      } else {
        console.log('‚ùå Error creating test user:', error.response?.data);
        setMessage('‚ùå Failed to create test user');
      }
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '20px',
      fontFamily: 'Arial, sans-serif',
      color: 'white',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    }}>
      <div style={{
        background: 'rgba(255, 255, 255, 0.1)',
        padding: '40px',
        borderRadius: '15px',
        textAlign: 'center',
        backdropFilter: 'blur(10px)',
        maxWidth: '500px',
        width: '100%'
      }}>
        <h1 style={{ fontSize: '2rem', marginBottom: '20px' }}>
          HRMS Lite - Backend Test
        </h1>
        
        <div style={{
          background: 'rgba(0, 0, 0, 0.2)',
          padding: '15px',
          borderRadius: '8px',
          marginBottom: '30px'
        }}>
          <p style={{ fontSize: '1.1rem', margin: 0 }}>{message}</p>
          {lastRegisteredUser && (
            <p style={{ fontSize: '0.9rem', margin: '10px 0 0 0', opacity: '0.8' }}>
              Last user: {lastRegisteredUser.email}
            </p>
          )}
        </div>

        <div style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
          <button 
            onClick={testWithNewUser}
            style={{
              padding: '15px',
              fontSize: '1rem',
              backgroundColor: '#28a745',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer'
            }}
          >
            Test with NEW User (Recommended)
          </button>

          <button 
            onClick={createTestUser}
            style={{
              padding: '15px',
              fontSize: '1rem',
              backgroundColor: '#ffc107',
              color: 'black',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer'
            }}
          >
            Create Test User (test@hrms.com)
          </button>

          <button 
            onClick={testExistingUser}
            style={{
              padding: '15px',
              fontSize: '1rem',
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer'
            }}
          >
            Test Login with Existing User
          </button>
        </div>

        <div style={{
          marginTop: '30px',
          padding: '15px',
          background: 'rgba(0, 0, 0, 0.2)',
          borderRadius: '8px',
          fontSize: '0.9rem'
        }}>
          <p><strong>Backend Status:</strong> ‚úÖ Running on port 5000</p>
          <p><strong>Frontend Status:</strong> ‚úÖ Running on port 3000</p>
          <p><strong>Database:</strong> ‚úÖ SQLite (persistent)</p>
        </div>

        <div style={{ marginTop: '20px', fontSize: '0.8rem', opacity: '0.7' }}>
          <p>Check browser console (F12) for detailed results</p>
        </div>
      </div>
    </div>
  );
}

export default App;
