import { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const result = await login(formData.email, formData.password);

    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.message);
    }

    setLoading(false);
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        {/* Header Section */}
        <div style={styles.header}>
          <div style={styles.logo}>
            <div style={styles.logoIcon}>üíº</div>
            <h1 style={styles.title}>HRMS Pro</h1>
          </div>
          <p style={styles.subtitle}>Streamlined Employee Management</p>
        </div>
        
        {/* Form Section */}
        <div style={styles.formSection}>
          <h2 style={styles.formTitle}>Welcome Back</h2>
          <p style={styles.formSubtitle}>Sign in to your HRMS account</p>
          
          {error && (
            <div style={styles.error}>
              <span style={styles.errorIcon}>‚ö†Ô∏è</span>
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit} style={styles.form}>
            <div style={styles.formGroup}>
              <label style={styles.label}>Email Address</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                style={styles.input}
                placeholder="Enter your email"
              />
            </div>

            <div style={styles.formGroup}>
              <label style={styles.label}>Password</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
                style={styles.input}
                placeholder="Enter your password"
              />
            </div>

            <button 
              type="submit" 
              style={{
                ...styles.button,
                ...(loading ? styles.buttonLoading : {})
              }}
              disabled={loading}
            >
              {loading ? (
                <>
                  <span style={styles.spinner}></span>
                  Signing In...
                </>
              ) : (
                'Sign In'
              )}
            </button>
          </form>

          <div style={styles.divider}>
            <span style={styles.dividerText}>New to HRMS Pro?</span>
          </div>

          <Link to="/register" style={styles.registerLink}>
            Create an account
          </Link>
        </div>

        {/* Features Section */}
        <div style={styles.features}>
          <h3 style={styles.featuresTitle}>Everything you need to manage your workforce</h3>
          <div style={styles.featuresGrid}>
            <div style={styles.feature}>
              <div style={styles.featureIcon}>üë•</div>
              <div style={styles.featureContent}>
                <h4 style={styles.featureTitle}>Employee Management</h4>
                <p style={styles.featureDescription}>Manage employee profiles and information</p>
              </div>
            </div>
            <div style={styles.feature}>
              <div style={styles.featureIcon}>üè¢</div>
              <div style={styles.featureContent}>
                <h4 style={styles.featureTitle}>Department Management</h4>
                <p style={styles.featureDescription}>Organize teams and departments</p>
              </div>
            </div>
            <div style={styles.feature}>
              <div style={styles.featureIcon}>üìä</div>
              <div style={styles.featureContent}>
                <h4 style={styles.featureTitle}>Attendance Tracking</h4>
                <p style={styles.featureDescription}>Monitor attendance and time-off</p>
              </div>
            </div>
            <div style={styles.feature}>
              <div style={styles.featureIcon}>üîê</div>
              <div style={styles.featureContent}>
                <h4 style={styles.featureTitle}>Role-Based Access</h4>
                <p style={styles.featureDescription}>Secure access controls</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '20px',
    fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif",
  },
  card: {
    background: 'white',
    padding: '48px',
    borderRadius: '24px',
    boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)',
    width: '100%',
    maxWidth: '480px',
    border: '1px solid rgba(255, 255, 255, 0.2)',
  },
  header: {
    marginBottom: '40px',
    textAlign: 'center',
  },
  logo: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '12px',
    marginBottom: '12px',
  },
  logoIcon: {
    fontSize: '2.5rem',
  },
  title: {
    fontSize: '2rem',
    fontWeight: '700',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    margin: 0,
  },
  subtitle: {
    fontSize: '1rem',
    color: '#6b7280',
    margin: 0,
    fontWeight: '500',
  },
  formSection: {
    marginBottom: '40px',
  },
  formTitle: {
    fontSize: '1.5rem',
    color: '#1f2937',
    marginBottom: '8px',
    fontWeight: '600',
  },
  formSubtitle: {
    fontSize: '0.875rem',
    color: '#6b7280',
    marginBottom: '32px',
  },
  form: {
    marginBottom: '24px',
  },
  formGroup: {
    marginBottom: '24px',
  },
  label: {
    display: 'block',
    marginBottom: '8px',
    color: '#374151',
    fontSize: '0.875rem',
