// context/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';

const AuthContext = createContext();

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Mock function - replace with your actual API call
  const login = async (email, password, requestedRole) => {
    try {
      // Simulate API call
      const response = await yourAuthAPI(email, password);
      
      if (response.success) {
        // Check if user has the requested role
        if (response.user.role !== requestedRole) {
          return {
            success: false,
            message: `Access denied. This portal is for ${requestedRole}s only.`
          };
        }
        
        setCurrentUser(response.user);
        return { success: true };
      } else {
        return response;
      }
    } catch (error) {
      return {
        success: false,
        message: 'Login failed. Please try again.'
      };
    }
  };

  // Your actual authentication API call
  const yourAuthAPI = async (email, password) => {
    // Replace this with your actual authentication API
    // This should return user data including their role
    const users = {
      'admin@company.com': { password: 'admin123', role: 'admin', name: 'Admin User' },
      'employee@company.com': { password: 'employee123', role: 'employee', name: 'John Doe' }
    };

    if (users[email] && users[email].password === password) {
      return {
        success: true,
        user: {
          email,
          role: users[email].role,
          name: users[email].name
        }
      };
    }
    
    return {
      success: false,
      message: 'Invalid email or password'
    };
  };

  const value = {
    currentUser,
    login,
    // ... other auth functions
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}
